mapper.update:
UPDATE test_api_job
job_name = #{jobName}, case_count = #{caseCount}, status = #{status}, curl = #{curl}, update_time = CURRENT_TIMESTAMP
WHERE id = #{id}

mapper.select:
SELECT id, job_name, case_count, status, curl, create_time, update_time
FROM test_api_job
WHERE id = #{id}

SELECT id, job_name, case_count, status, curl, create_time, update_time
FROM test_api_job
WHERE status = #{status}
